//直接获取区块链上的众筹的智能合约对象
import web3 from './web3';//插入了用户浏览器web3版本的web3对象

const address = '0x5Cb0672004017A8f93bb4Def4c0731DD8bb857c0';//合约的地址
// 换行快捷键：Ctrl+Shift+J(多行变一行),格式化代码可以用Ctrl+Alt+L（一行变成多行拥有一定格式的代码）
const abi = [{
    "constant": false,
    "inputs": [{"name": "_projectName", "type": "string"}, {
        "name": "_supportMoney",
        "type": "uint256"
    }, {"name": "_goalMoney", "type": "uint256"}],
    "name": "deploy",
    "outputs": [],
    "payable": false,
    "stateMutability": "nonpayable",
    "type": "function"
}, {
    "constant": true,
    "inputs": [{"name": "", "type": "uint256"}],
    "name": "fundings",
    "outputs": [{"name": "", "type": "address"}],
    "payable": false,
    "stateMutability": "view",
    "type": "function"
}];

//获取众筹的智能合约对象（通过传入abi和合约地址获取）
const fund = new web3.eth.Contract(abi, address);

//导出（暴露）fund对象供其他文件引用
export default fund;
